        },
        body: JSON.stringify({
          name: name,
          description: description,
          category: category,
          owner: this.state.wallet.address
        })
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || 'DApp creation failed');
